import { Component, Input, OnInit } from '@angular/core';
import { GENRE } from 'src/app/genre';
import { Movie } from 'src/app/interface/movie.interface';
import { StorageService } from '../services/storage.service';


@Component({
  selector: 'app-film',
  templateUrl: './film.component.html',
  styleUrls: ['./film.component.scss']
})
export class FilmComponent implements OnInit {
  genres = GENRE;

  @Input() film!: Movie;

  constructor(private storageService: StorageService) {}

  ngOnInit(): void {

  }

  saveFilm(id: any) {
    this.storageService.saveFilm(id);
  }

}
<div class="container">
    <img class="img" [src]="film.url" alt="">
    <h2 class="name">{{film.name}}</h2>
    <div class="genre">{{film.genre}}</div>
    <span class="year">{{film.year}}</span>
    <button (click)="saveFilm(film.id)" class="mdc-icon-button material-icons">
        <div class="mdc-icon-button__ripple"></div>
        favorite_border
    </button>
</div>
