import { Component, OnInit } from '@angular/core';
import { BestMovieService } from './best-movie.service';
import { Movie } from './interface/movie.interface';
import {MDCIconButtonToggle} from '@material/icon-button';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
const iconToggle = new MDCIconButtonToggle(document.querySelector('.mdc-icon-button'));z
export class AppComponent implements OnInit {

  moviesList: Movie [] = [];

  constructor(private BestMovieService: BestMovieService) {}

  ngOnInit(): void {

    this.BestMovieService.getMovie().subscribe(({data}: any) => {

      console.log(data);

      const {movies} = data;
      this.moviesList = movies.map((item: any) => {
        return {
          url: item.url,
          name: item.name,
          genre: item.genre,
          year: item.year,
          id: item.id,
          description: item.description,
        };
      });
    });
  }
}
