import { Component, Input, OnInit } from '@angular/core';
import { Movie } from 'src/app/interface/movie.interface';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.scss']
})
export class SearchComponent{

  @Input () film!: Movie[];

  searchTerm: string = ''

  constructor() {}

  handleSearch (e: KeyboardEvent) {
    e.key === 'enter';
    this.film = this.film.filter(data => data.name.toLowerCase().includes(this.searchTerm.toLowerCase()))
  }

}
<div class="search">
    <input type="text" placeholder="Введите название" (keydown)="handleSearch()">

</div>
