import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { GENRE } from './genre';

@Injectable({
  providedIn: 'root'
})
export class BestMovieService {

  constructor(private http: HttpClient) { }

  getMovie() {
    return this.http.get('../assets/data.json');
  }

  parseResponse({data}: any) {
    let genreFilm = GENRE;
    const {movies} = data;
      return movies.map((item: any) => {
        return {
          url: item.url,
          name: item.name,
          year: item.year,
          id: item.id,
          description: item.description,
          genre: item.genre.forEach((elem: any) => {
            const find = genreFilm.find((item: any) => item.id === elem);
            // console.log(find);
            item.genre = find?.name.ma
            console.log(item.genre)
            return item.genre
          }),
          // genre: item.genre.filter((item: any) => {
          //   return {
          //     item
          //   }
          // })
        };
      });
  }
}
