import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.scss']
})
export class SearchComponent{

  @Output() findFilms = new EventEmitter<{searchTerm: string}>();

  searchTerm: string = ''

  constructor() {}

  handleSearch (e: KeyboardEvent) {
    if(e.key === 'Enter') {
      this.findFilms.emit({searchTerm: this.searchTerm})
    }
  }

}
<div class="form-group">
    <input class="form-control" type="text" name="searchTerm" [(ngModel)]="searchTerm" placeholder="Введите название" (keydown)="handleSearch($event)" #ctrl="ngModel" required />
</div>
