import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-filters',
  templateUrl: './filters.component.html',
  styleUrls: ['./filters.component.scss']
})
export class FiltersComponent {
  @Output() filterFilms = new EventEmitter<{genre: any}>();

  handleFilter(genre: any) {

    this.filterFilms.emit({ genre })

  }
}
<div class="container">
    <select name="" id="">
        <option value="Все">Все</option>
        <option value="Драма" (onclick)="handleFilter('драма')">Драма</option>
        <option value="Биография">Биография</option>
        <option value="История">История</option>
        <option value="Фэнтези">Фэнтези</option>
        <option value="Приключения">Приключения</option>
        <option value="Боевик">Боевик</option>
        <option value="мультфильм" (onclick)="handleFilter('мультфильм')">мультфильм</option>
        <option value="Комедия">Комедия</option>
        <option value="Триллер">Триллер</option>
        <option value="Детектив">Детектив</option>
        <option value="Фантастика">Фантастика</option>
    </select>

    <mat-select>
        <mat-option></mat-option>
    </mat-select>

    <!-- <button (click)="handleFilter()"></button> -->
</div>
